mapper.delete:
UPDATE sys_org SET fg_valid = #{status}
WHERE
id_org in (

    #{item}

)

mapper.update:
UPDATE sys_org_reg SET sd_reg = #{status}, confirmor = #{confirmor}, gmt_confirm = now(), operator = #{operator}, gmt_modify = now() WHERE id_reg in (
#{item}
)

UPDATE sys_org SET fg_active = #{status}, gmt_valid = null, operator = #{operator}, gmt_modify = now() WHERE id_org in (
#{item}
)

mapper.select:
SELECT count(1) FROM sys_org WHERE fg_valid = '0'
AND name like concat(#{name},'%')
AND fg_active = #{fgActive}
AND fg_valid = #{fgValid}
AND gmt_valid is not null AND fg_active='1' AND gmt_valid <= #{gmtValid}

SELECT id_org, name , des , addr , phone , email , fg_plat , fg_active , fg_valid , DATE_FORMAT(gmt_valid,'%Y-%m-%d') as gmtValid, sort , creator , operator , gmt_create , gmt_modify FROM sys_org WHERE fg_valid = '0'
AND name like concat(#{name},'%')
AND fg_active = #{fgActive}
AND fg_valid = #{fgValid}
AND gmt_valid is not null AND fg_active='1' AND gmt_valid <= #{gmtValid}
ORDER BY sort desc limit #{offset}, #{limit}

SELECT
    count(1)
FROM
    sys_org_reg sor left join sys_org so on so.id_org = sor.id_org
WHERE
    1 = 1

    AND so.name like concat(#{name},'%')


    AND sor.sd_reg = #{sdReg}


    AND sor.gmt_apply >= #{gmtApplyStart}
    AND sor.gmt_apply <= #{gmtApplyEnd}

SELECT sor.id_reg, sor.sd_reg,sor.applyor, sor.gmt_apply, sor.confirmor, sor.gmt_confirm, so.id_org, so.name , so.des , so.addr , so.phone , so.email , so.fg_plat , so.fg_active , so.fg_valid , DATE_FORMAT(so.gmt_valid,'%Y-%m-%d') as gmtValid, so.sort , so.creator , so.operator , so.gmt_create , so.gmt_modify FROM sys_org_reg sor left join sys_org so on so.id_org = sor.id_org WHERE 1 = 1
AND so.name like concat(#{name},'%')
AND sor.sd_reg = #{sdReg}
AND sor.gmt_apply >= #{gmtApplyStart} AND sor.gmt_apply <= #{gmtApplyEnd}
