mapper.select:
SELECT ui.user_id , ui.username , ui.email , ui.sex , ui.phone_no , ui.enabled , ui.is_first , ui.real_name , ui.last_login_time , ui.is_deleted , ui.role_type , ui.gmt_create , ui.gmt_modify , ui.operator , ui.id_org, ui.idcard, ui.user_cd, ui.photo_addr, sr.name as roleName FROM user_info ui left join user_role ur on ui.user_id = ur.user_id and ur.is_deleted = 'N' inner join sys_role sr on ur.role_id = sr.role_id and sr.code != 'MCST'
LEFT JOIN( SELECT a.user_id , min(b.level) as level FROM user_role a LEFT JOIN sys_role b ON a.role_id = b.role_id WHERE a.is_deleted = 'N' group by a.user_id ORDER BY b.level DESC ) temp ON ui.user_id = temp.user_id
WHERE ui.is_deleted = 'N'
and ui.username like concat(#{conditionValue},'%')
and ui.real_name like concat(#{conditionValue},'%')
and ui.phone_no like concat(#{conditionValue},'%')
and ui.id_org = #{idOrg}
and ur.role_id = #{roleId}
and ui.username != 'super' limit #{offset}, #{limit}

SELECT
    count(1)
FROM
    user_info ui
INNER JOIN user_role ur ON ui.user_id = ur.user_id and ur.is_deleted = 'N' AND ur.role_id = #{roleId}
WHERE
     ui.phone_no = #{phoneNo}
AND ui.idcard = #{idCard}
and ui.is_deleted = 'N'

    and ui.username != #{username}
